<?php ob_start("minifier");function minifier($code) {    $search = array(                  // Remove whitespaces after tags        '/\>[^\S ]+/s',                  // Remove whitespaces before tags        '/[^\S ]+\</s',                  // Remove multiple whitespace sequences        '/(\s)+/s',                  // Removes comments        '/<!--(.|\s)*?-->/'    );    $replace = array('>', '<', '\\1');    $code = preg_replace($search, $replace, $code);    return $code;}$devicetye=devicedetector($_SERVER['HTTP_USER_AGENT']);     if($devicetye=='mobile'){      $image1='generic-img1-mob.jpg';           }else{      $image1='generic-img1.jpg';           }?>            <!--generic-img2-mob.jpg';-->			<div role="main" class="main">				<!-- Top Carousel -->				<?php if($devicetye!='mobile'){ ?>				<div id="carouselExampleControls" class="carousel slide" data-ride="carousel" data-interval="3000">  <div class="carousel-inner">    <div class="carousel-item active">      <img class="d-block w-100" src="<?php echo base_url() ?>images/media/<?php echo $image1 ?>" alt="First slide" loading='lazy'>    </div>          </div>    <?php $this->load->view('site/booking-widget');?></div><?php } ?>				<!-- Top Carousel -->				<!-- Brand page content starts here -->				<section class="overview-container popular-destinations">  			<div class="container inner-page-pad pt-3"> 						<div class="col-md-12">                	 					 						<div class="breadcrumb"><span class="pr-1"><a href="<?php echo base_url() ?>">Home</a></span> > <span class="pl-1 pr-1"> <span class="pl-1">Media & News</span></div> 					<div class="row mb-3 headings"> 					 					<div class="col-md-12">	                	<h3 class="mb-2">Media & News</h3>                	                	</div>                	</div>                	                	<?php foreach($medialist as $r) {?>                      <div class="news-items  mb-4">                      <div class